  transform: scale(1.1);
  cursor: pointer;
  text-align: center;
  
  a {
    transition: all 400ms ease;
    text-decoration: none;
    margin: .3rem;
    color: #000;
  }
}

.project-section__btn--btn1 {
  background: #be976b;

  &:hover {
    background: #9c7244;
    color: #ccc;

    a {
      color: #ccc;
    }
  }
}

.project-section__btn--btn2 {
  background: #8da1a9;

  &:hover {
    background: #2d5262;
    color: #ccc;

    a {
      color: #ccc;
    }
  }
